import fs from 'fs';
import path from 'path';

export default function createConfigFile(configPath, configFilename) {
	try {
		fs.writeFileSync(path.resolve(configPath, configFilename), '{}', {
			encoding: 'utf8',
			flag: 'wx',
		});
	} catch (_error) {
		// Do nothing, file might already exist
	}
}
