export default async function editorVersionsCommand(req, res) {
	res.caption(req.command.getLongName());

	const all = req.options.all;
	const destroySpinner = res.spinner(
		`Retrieving ${all ? 'all ' : ''}Fonto Editor versions...`
	);
	const sdkVersions = await req.fdt.license.getVersionsForProduct('editor');
	destroySpinner();
	res.caption('Fonto Editor versions');
	res.list(
		(all ? sdkVersions.getAll() : sdkVersions.getAllStable()).map(
			(version) => version.format()
		),
		'-'
	);
}
