import path from 'path';

export default (moduleRegistration, editorCommand, editorPre770ModulePath) => {
	editorCommand
		.addHiddenCommand(
			'versions',
			path.resolve(
				editorPre770ModulePath || moduleRegistration.getPath(),
				'src',
				'command.versions.controller.js',
			),
		)
		.setDescription('List the available Fonto Editor versions.')

		.setRequiresLicenseValidation()
		.addRequiredProductLicenses(['editor'])

		.addHiddenOption(
			'all',
			undefined,
			'Include nightly and prerelease versions.'
		)

		.addExample(
			`${moduleRegistration.getAppInfo().name} editor versions`,
			'Request the available versions for Fonto Editor.'
		);
};
