import express from 'express';

import configureBrowsePostRouteHandler from './configureBrowsePostRouteHandler.js';

// eslint-disable-next-line new-cap
const connectorsCmsStandardBrowseRouter = express.Router();

function configureConnectorsCmsStandardBrowseRouter() {
	connectorsCmsStandardBrowseRouter
		.route('/connectors/cms/standard/browse')
		.post(configureBrowsePostRouteHandler());

	return connectorsCmsStandardBrowseRouter;
}

export default configureConnectorsCmsStandardBrowseRouter;
