import express from 'express';

import configureHeartbeatGetRouteHandler from './configureHeartbeatGetRouteHandler.js';

const connectorsCmsStandardHeartbeatRouter = express.Router();

function configureConnectorsCmsStandardHeartbeatRouter(config) {
	connectorsCmsStandardHeartbeatRouter
		.route('/connectors/cms/standard/heartbeat')
		.get(configureHeartbeatGetRouteHandler(config));

	return connectorsCmsStandardHeartbeatRouter;
}

export default configureConnectorsCmsStandardHeartbeatRouter;
