import express from 'express';

import configureDebugRouteHandler from './configureDebugRouteHandler.js';

const connectorsCmsStandardRouter = express.Router();

function configureConnectorsCmsStandardRouter(config) {
	connectorsCmsStandardRouter
		.route('/connectors/cms/standard')
		.get(configureDebugRouteHandler(config))
		.post(configureDebugRouteHandler(config));

	return connectorsCmsStandardRouter;
}

export default configureConnectorsCmsStandardRouter;
