import referenceRepository from './referenceRepository.js';

export default function configureReferenceCreatePostRouteHandler(_config) {
	return (req, res) => {
		const reference = referenceRepository.create(
			req.body.type,
			req.body.target,
			req.body.title,
			req.body.description,
			req.body.metadata
		);

		return res
			.status(201)
			.set('content-type', 'application/json; charset=utf-8')
			.json(reference);
	};
}
