import express from 'express';

import configureHealthGetRouteHandler from './configureHealthGetRouteHandler.js';

// eslint-disable-next-line new-cap
const connectorsConnectorsHealthRouter = express.Router();

export default function configureConnectorsHealthRouter() {
	connectorsConnectorsHealthRouter
		.route('/connectors/health')
		.get(configureHealthGetRouteHandler());

	return connectorsConnectorsHealthRouter;
}
