module.exports = function getClosestNodeWithOffset(path) {
	const indexInContainer = path.container.indexOf(path.node);
	const containerChildCount = path.container.length;
	let offset = 0;
	if (indexInContainer < containerChildCount - 2) {
		// Next sibling.
		offset = 1;
	} else if (containerChildCount > 1) {
		// Previous sibling.
		offset = -1;
	}

	return {
		offset,
		node: path.container[indexInContainer + offset],
	};
};
