function isInvalidTextLoaderImportDeclaration(path) {
	// Has an import path to a json file and is prefixed with text!
	//
	// Correct:
	//           import module from 'text!./path/to/file.json';
	// path      ^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^^    (ImportDeclaration)
	// importPath                   ^^^^^^^^^^^^^^^^^^^^^^^^^^     (StringLiteral)
	const pathNodeSourceValue = path.node.source.value;
	if (
		pathNodeSourceValue.startsWith('text!') &&
		pathNodeSourceValue.endsWith('.json')
	) {
		return true;
	}

	return false;
}

module.exports = isInvalidTextLoaderImportDeclaration;
