import path from 'path';

export default (moduleRegistration, convertCommand, editorModulePath) => {
	convertCommand
		.addCommand(
			'package',
			path.resolve(
				editorModulePath || moduleRegistration.getPath(),
				'src',
				'commands',
				'command.convert.package.controller.js',
			),
		)
		.setDescription(
			'Convert a Fonto Editor configuration package from pre Fonto Editor 7.7 to a Fonto Editor 7.7 codebase.'
		)

		.addOption(
			'no-overwrite',
			undefined,
			'Run the conversion on a copy of the working directory.'
		)
		.addOption(
			'no-save-warnings',
			undefined,
			'Do not save the warning output of the convert command.'
		);
};
