/**
 * This loader replaces an asset with a new JavaScript class instance which
 * wraps the raw data of the assets.
 */
module.exports = function symbolLoader(input) {
	this.cacheable(true);

	const options = this.getOptions();
	const className = options.className;
	const importPath = options.importPath;

	const code =
		`import ${className} from '${importPath}';\n` +
		`export default new ${className}(${JSON.stringify(input)});`;

	return code;
};
