import path from 'path';

export default async (moduleRegistration) => {
	moduleRegistration
		.registerCommand(
			'operation',
			path.resolve(
				moduleRegistration.getPath(),
				'src',
				'command.operation.controller.js'
			)
		)
		.setDescription('Output information about a specific operation.')

		.setRequiresEditorRepository()
		.setRequiresEditorVersionToMatchFdt()

		.addOption(
			new moduleRegistration.MultiOption('roots')
				.setShort('r')
				.setDescription('Root folder(s).')
		)

		.addParameter('operation', 'The operation name.', true)

		.addExample(
			`${moduleRegistration.getAppInfo().name} operation delete-node`,
			'Output information about the "delete-node" operation.'
		);
};
