export default async function createSimpleTypesTable(moduleRegistration) {
	const FdtTable = await moduleRegistration.getExportFromModule(
		'@fontoxml/fontoxml-development-tools-module-shared',
		'FdtTable',
	);

	return new FdtTable(moduleRegistration, [
		{
			name: 'name',
			default: true,
			label: 'Name',
			value: (element) => element.localName,
		},
		{
			name: 'variety',
			default: true,
			label: 'Variety',
			value: (element) => element.variety,
		},
		{
			name: 'format',
			default: true,
			label: 'Format',
			value: (element) => element.getSimpleTypeString(),
		},
	]);
}
