import os from 'os';

const formatterPerRestrictionType = {
	enumeration: (value) => value.join(os.EOL),
	// fractionDigits,
	// length,
	// maxExclusive,
	// maxInclusive,
	// maxLength,
	// minExclusive,
	// minInclusive
	// minLength,
	// pattern,
	// totalDigits,
	// whiteSpace
};

function formatType(restrictionType) {
	restrictionType = restrictionType.replace(/([A-Z])/g, (match) => {
		return ` ${match.charAt(0).toLowerCase()}${match.substr(1)}`;
	});
	restrictionType = restrictionType.replace(/^\s*/, '');
	return restrictionType.charAt(0).toUpperCase() + restrictionType.substr(1);
}

function formatValue(restrictionType, value) {
	return formatterPerRestrictionType[restrictionType]
		? formatterPerRestrictionType[restrictionType](value)
		: value;
}

export default {
	formatType,
	formatValue,
};
