/** @typedef {import('../addBackendVersionsCommands').AddBackendVersionsCommandsOptions} AddBackendVersionsCommandsOptions */

/**
 * @param {AddBackendVersionsCommandsOptions} options
 *
 * @returns {(req: FdtRequest, res: FdtResponse) => Promise<void>}
 */
export default function backendVersionCommandFactory(_options) {
	return async function backendVersionCommand(req, res) {
		let format = req.options.format;
		if (format === true) {
			format = 'full';
		}
		res.raw(req.fdt.backendAppRepository.sdkVersion.format(format));
	};
}
